import React from "react";

const ProductForm = () => {
  const [product, setProduct] = useState([]);

  const handleInput = () => {};
  return (
    <div className="ProductFormCard__Container ">
      <h2 className="mt-5">Create New Product</h2>
      <div className="card-body  ">
        <form onSubmit={submitProductDetails}>
          <div className="form-row ">
            <div className="col m-3 form-field">
              <input
                type="text"
                required={true}
                className="form-control"
                placeholder="Product name"
                name="product_name"
                value={userInput.product_name}
                onChange={handleInput}
              />
            </div>
            <div className="col m-3">
              <input
                type="number"
                required={true}
                className="form-control"
                placeholder="Original Price"
                name="original_price"
                value={userInput.original_price}
                onChange={handleInput}
              />
            </div>
            <div className="col m-3">
              <input
                type="number"
                required={true}
                className="form-control"
                placeholder="Sale Price"
                name="sale_price"
                value={userInput.sale_price}
                onChange={handleInput}
              />
            </div>
            <div className="col m-3">
              <input
                type="number"
                required={true}
                className="form-control"
                placeholder="Product Type"
                name="product_type"
                value={userInput.product_type}
                onChange={handleInput}
              />
            </div>
            <div className="col m-3">
              <textarea
                type="text"
                required={true}
                className="form-control"
                placeholder="Description"
                name="description"
                value={userInput.description}
                onChange={handleInput}
              />
            </div>
            <div className="col m-3">
              <input
                type="submit"
                className="btn btn-primary  w-100 "
                value="Create"
              />
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProductForm;
