import React, { useState, useEffect } from "react";
import ProductForm from "./components/ProductForm";
import DataTable from "react-data-table-component";
import axios from "axios";
import "./App.css";

const productUrl = "https://lobster-app-ddwng.ondigitalocean.app/product/list";

function App(props) {
  const [prod, setProd] = useState("");
  const [allproducts, setAllProducts] = useState([]);
  console.log("all", allproducts);

  useEffect(() => {
    getAllData();
  }, []);

  const getAllData = () => {
    axios
      .get(productUrl, {
        headers: { api_key: "Z9Q7WKEY7ORGBUFGN3EG1QS5Y7FG8DU29GHKKSZH" }, // api key  for get data
      })
      .then((response) => {
        console.log(response.data.message);
        setAllProducts(response.data.message);
      })
      .catch((error) => {
        console.log(error.message);
      });
  };

  console.log(prod);

  const columns = [
    {
      name: "Sno",
      selector: (row) => row._id,
    },
    {
      name: "Product Name",
      selector: (row) => row.product_name,
      sortable: true,
    },
    {
      name: "Original Price",
      selector: (row) => row.original_price,
    },
    {
      name: "Sale Price",
      selector: (row) => row.sale_price,
    },
    {
      name: "Product Type",
      selector: (row) => row.product_type,
    },
    {
      name: "Description",
      selector: (row) => row.description,
    },
  ];

  return (
    <div className="App ">
      <ProductForm setdata={setProd} />
      <hr />
      <DataTable
        title="All Products"
        columns={columns}
        data={allproducts}
        pagination
        fixedHeader
        fixedHeaderScrollHeight="400px"
      />
    </div>
  );
}

export default App;
