import React, { useState, useEffect } from "react";
import ProductForm from "./components/ProductForm";
import DataTable, { createTheme } from "react-data-table-component";
import axios from "axios";
import "./App.css";

const productUrl = "https://lobster-app-ddwng.ondigitalocean.app/product/list";

function App(props) {
  const [prod, setProd] = useState("");
  const [search, setSearch] = useState("");
  const [allproducts, setAllProducts] = useState([]);
  const [filterdProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    getAllData();
  }, []);

  useEffect(() => {
    const result = allproducts.filter((eachProd) => {
      return eachProd.product_name.toLowerCase().match(search.toLowerCase());
    });
    setFilteredProducts(result);
  }, [search]);

  const getAllData = () => {
    axios
      .get(productUrl, {
        headers: { api_key: "Z9Q7WKEY7ORGBUFGN3EG1QS5Y7FG8DU29GHKKSZH" }, // api key  for get data
      })
      .then((response) => {
        console.log(response.data.message);
        setAllProducts(response.data.message);
        setFilteredProducts(response.data.message);
      })
      .catch((error) => {
        console.log(error.message);
      });
  };

  const columns = [
    {
      name: "Sno",
      selector: (row) => row._id,
    },
    {
      name: "Product Name",
      selector: (row) => row.product_name,
      sortable: true,
    },
    {
      name: "Product No",
      selector: (row) => row._id,
      sortable: true,
    },
    {
      name: "Original Price",
      selector: (row) => row.original_price,
    },
    {
      name: "Sale Price",
      selector: (row) => row.sale_price,
    },
    {
      name: "Product Type",
      selector: (row) => row.product_type,
    },
    {
      name: "Description",
      selector: (row) => row.description,
    },
  ];

  createTheme(
    "solarized",
    {
      text: {
        primary: "#268bd2",
        secondary: "#2aa198",
      },
      background: {
        default: "dark",
      },
      context: {
        background: "#cb4b16",
        text: "#FFFFFF",
      },
      divider: {
        default: "#073642",
      },
      action: {
        button: "rgba(0,0,0,.54)",
        hover: "#fff",
        disabled: "rgba(0,0,0,.12)",
      },
    },
    "dark"
  );

  return (
    <div className="App ">
      <ProductForm setdata={setProd} />
      <hr />
      <DataTable
        title="All Products"
        columns={columns}
        data={filterdProducts}
        pagination
        fixedHeader
        height="400px"
        selectableRowsHighlight
        highlightOnHover
        subHeader
        center
        keyField="_id"
        pointerOnHover
        theme="solarized"
        subHeaderComponent={
          <input
            type="text"
            placeholder="Search here"
            className="w-40 form-control"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        }
        subHeaderAlign="right"
      />
    </div>
  );
}

export default App;
