import React, { useState, useEffect } from "react";
import ProductForm from "./components/ProductForm";
import DataTable, { createTheme } from "react-data-table-component";
import axios from "axios";
import "./App.css";

const productUrl = "https://lobster-app-ddwng.ondigitalocean.app/product/list";

function App(props) {
  const [prod, setProd] = useState("");
  const [search, setSearch] = useState("");
  const [allproducts, setAllProducts] = useState([]);
  const [filterdProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    getAllData();
  }, []);

  useEffect(() => {
    const result = allproducts.filter((eachProd) => {
      return eachProd.product_name.toLowerCase().match(search.toLowerCase());
    });
    setFilteredProducts(result);
  }, [search]);

  const getAllData = () => {
    axios
      .get(productUrl, {
        headers: { api_key: "Z9Q7WKEY7ORGBUFGN3EG1QS5Y7FG8DU29GHKKSZH" }, // api key  for get data
      })
      .then((response) => {
        console.log(response.data.message);
        setAllProducts(response.data.message);
        setFilteredProducts(response.data.message);
      })
      .catch((error) => {
        console.log(error.message);
      });
  };
  useEffect(() => {
    axios
      .post(
        url,
        {
          product_name: userInput.product_name,
          original_price: userInput.original_price,
          sale_price: userInput.sale_price,
          product_type: userInput.product_type,
          description: userInput.description,
          date_n_time: userInput.date_n_time,
        },
        {
          headers: {
            api_key: "Z9Q7WKEY7ORGBUFGN3EG1QS5Y7FG8DU29GHKKSZH", //apikey for post
          },
        }
      )
      .then((response) => {
        console.log(response);
        getAllData();
        alert("Product Created Successfully");
        setUserInput({
          product_name: "",
          original_price: "",
          sale_price: "",
          product_type: "",
          description: "",
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }, [allproducts]);

  const columns = [
    {
      name: "Sno",
      selector: (row) => row._id,
    },
    {
      name: "Product Name",
      selector: (row) => row.product_name,
      sortable: true,
    },
    {
      name: "Product No",
      selector: (row) => row._id,
      sortable: true,
    },
    {
      name: "Original Price",
      selector: (row) => row.original_price,
    },
    {
      name: "Sale Price",
      selector: (row) => row.sale_price,
    },
    {
      name: "Product Type",
      selector: (row) => row.product_type,
    },
    {
      name: "Description",
      selector: (row) => row.description,
    },
  ];

  createTheme(
    "solarized",
    {
      text: {
        primary: "#268bd2",
        secondary: "#2aa198",
      },
      background: {
        default: "#101339;",
      },
      context: {
        background: "#cb4b16",
        text: "#FFFFFF",
      },
      divider: {
        default: "#073642",
        borderRadius: "5px",
      },
      action: {
        button: "rgba(0,0,0,.54)",
        hover: "#fff",
        disabled: "rgba(0,0,0,.12)",
      },
    },
    "dark"
  );
  const customStyles = {
    table: {
      style: {
        backgroundColor: "#101339",
        border: "1px solid #fff",
      },
    },
    headCells: {
      style: {
        fontWeight: "700",
        fontSize: "15px",
        color: "#fff",
        border: "2px solid #fff",
        opacity: "0.8",
        textAlign: "center",
      },
    },
    rows: {
      style: {
        fontSize: "15px",
        color: "#fff",
        opacity: "0.7",
      },
    },
    cells: {
      style: {
        border: "0.5px solid #fff",
      },
    },
  };

  return (
    <div className="App ">
      <ProductForm setdata={setProd} />
      <hr />
      <DataTable
        title="All Products"
        columns={columns}
        width="50vh"
        data={filterdProducts}
        pagination
        fixedHeader
        height="400px"
        selectableRowsHighlight
        highlightOnHover
        subHeader
        center
        keyField="_id"
        pointerOnHover
        theme="solarized"
        subHeaderComponent={
          <input
            type="text"
            placeholder="Search here"
            className="formControl"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        }
        subHeaderAlign="right"
        customStyles={customStyles}
      />
    </div>
  );
}

export default App;
