import React, { useState } from "react";
import "./products.css";

const ProductForm = (props) => {
  const [userInput, setUserInput] = useState({
    product_name: "",
    original_price: "",
    sale_price: "",
    product_type: "",
    description: "",
  });

  const handleInput = (e) => {
    setUserInput({ ...userInput, [e.target.name]: e.target.value });
  };

  const submitProductDetails = (e) => {
    e.preventDefault();
    props.setdata(userInput);
  };

  return (
    <div className="ProductFormCard__Container">
      <pre>{JSON.stringify(userInput)}</pre>
      <h2 className="mt-5">{props.heading}</h2>
      <div className="card-body  ">
        <form onSubmit={submitProductDetails}>
          <div className="form-row ">
            <div className="col form-field">
              <input
                type="text"
                required={true}
                className="form-control"
                placeholder="Product name"
                name="product_name"
                value={userInput.product_name}
                onChange={handleInput}
              />
            </div>
            <div className="col m-3">
              <input
                type="number"
                required={true}
                className="form-control"
                placeholder="Original Price"
                name="original_price"
                value={userInput.original_price}
                onChange={handleInput}
              />
            </div>
            <div className="col m-5">
              <input
                type="number"
                required={true}
                className="form-control"
                placeholder="Sale Price"
                name="sale_price"
                value={userInput.sale_price}
                onChange={handleInput}
              />
            </div>
            <div className="col m-3">
              <input
                type="number"
                required={true}
                className="form-control"
                placeholder="Product Type"
                name="product_type"
                value={userInput.product_type}
                onChange={handleInput}
              />
            </div>
            <div className="col m-3">
              <textarea
                type="text"
                required={true}
                className="form-control"
                placeholder="Description"
                name="description"
                value={userInput.description}
                onChange={handleInput}
              />
            </div>
            <div className="col m-3">
              <input
                type="submit"
                className="btn btn-primary  w-100 "
                value="Create"
              />
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProductForm;
